import { useEffect, useState } from "react";

export default function Discussion() {
  const [discussions, setDiscussions] = useState([]);

  useEffect(() => {
    fetch("http://localhost:4000/")
      .then((data) => data.json())
      .then((data) => setDiscussions(data));
  }, []);

  return discussions.map((discussion) => {
    <section className="discussion__wrapper" id={discussion.id}>
      <ul className="discussions__container">
        <li className="discussion__container">
          <div className="discussion__avatar--wrapper">
            <img
              className="discussion__avatar--image"
              src="https://avatars.githubusercontent.com/u/12145019?s=64&u=5c97f25ee02d87898457e23c0e61b884241838e3&v=4"
              alt="avatar of kimploo"
            />
          </div>

          <div className="discussion__content">
            <h2 className="discussion__title">
              <a href="https://github.com/codestates-seb/agora-states-fe/discussions/6"></a>
            </h2>
            <div className="discussion__information"></div>
          </div>

          <div className="discussion__answered">
            <p></p>
          </div>
        </li>
      </ul>
    </section>;
  });
}
