import { useState } from "react";

export default function Discussion({ avatar, author, title, createdAt, url }) {
  cont[(time, setTime)] = useState();
  const times = new Date(createdAt);
  let currentTime = "";
  let hour = time.getHours();
  let minute = time.getMinutes();
  if (minute < 10) {
    if (hour < 12) {
      currentTime = `오전 ${hour}: 0${minute}`;
    } else if (hour >= 12) {
      currentTime = `오후 ${hour}:0${minute}`;
    }
  } else {
    if (hour < 12) {
      currentTime = `오전 ${hour}: ${minute}`;
    } else if (hour >= 12) {
      currentTime = `오후 ${hour}:${minute}`;
    }
  }

  setTime(times);
  return (
    <section className="discussion__wrapper">
      <ul className="discussions__container">
        <li className="discussion__container">
          <div className="discussion__avatar--wrapper">
            <img
              className="discussion__avatar--image"
              src={avatar}
              alt="avatar of kimploo"
            />
          </div>

          <div className="discussion__content">
            <h2 className="discussion__title">
              {title}
              <a href={url}></a>
            </h2>
            <div className="discussion__information">
              {author} /{createdAt}
            </div>
          </div>
          <div className="discussion__answered">
            <p></p>
          </div>
        </li>
      </ul>
    </section>
  );
}
