import { isVisible } from "@testing-library/user-event/dist/utils";
import { BsFillXCircleFill } from "react-icons/bs";

export default function FormContainer({ setViisiable }) {
  const clickHandler = (e) => {
    if ((e.className = "icon-wrapper")) {
      setViisiable(!isVisible);
    }
  };

  function registerDiscussion(event) {
    event.preventDefault();
    const date = new Date();
    const url = "http://localhost:4000/";

    const data = {
      createdAt: "2022-05-02T06:48:26Z",
      updatedAt: "2022-05-02T06:48:26Z",
      title: "UrClass에서 제공하는 이미지를 블로그에 인용해도 괜찮을까요?",
      url: "https://github.com/codestates-seb/agora-states-fe/discussions/16",
      author: "Topbin2",
      answer: {
        id: "DC_kwDOHOApLM4AKMdX",
        createdAt: "2022-05-02T09:47:42Z",
        url: "https://github.com/codestates-seb/agora-states-fe/discussions/16#discussioncomment-2672471",
        author: "Kingsenal",
        bodyHTML:
          '<p dir="auto">안녕하세요 <a href="https://github.com/Topbin2" data-octo-dimensions="link_type:self" data-octo-click="hovercard-link-click" data-hovercard-url="/users/Topbin2/hovercard" data-hovercard-type="user" class="user-mention notranslate">@Topbin2</a> 님,</p>\n<p dir="auto">질문주신 내용의 경우는 현재 저작권 관련 문제가 있는 관계로 내부 논의가 필요한 과정입니다.<br>\n그렇기 때문에 내부 논의가 거쳐야 함으로, 24시간 이후에 답변이 나갈 수 있습니다.<br>\n최대한 빠르게 답변을 남겨 드릴테니 조금만 시간을 가지고 기다려주시면 감사드리겠습니다!</p>\n<p dir="auto">감사합니다.🚀<br>\n코드스테이츠 교육 엔지니어 권준혁</p>',
        avatarUrl: "https://avatars.githubusercontent.com/u/79903256?s=64&v=4",
      },
      bodyHTML:
        '<p dir="auto">블로그에 그날 배운 내용을 정리할 때 UrClass 에 있는 이미지를 인용해도 괜찮을까요 ??<br>\n저작권 문제가 있을 수 있어서 여쭤봅니다 !</p>',
      avatarUrl:
        "https://avatars.githubusercontent.com/u/95295766?s=64&u=85d493e0be0d2ca55965efd9f6c5b268c9dca168&v=4",
    };

    fetch(url, {
      method: "POST",
      body: JSON.stringify(data),
    });
  }

  // document.querySelector('form').addEventListener('submit', registerFood);

  return (
    <section className="form__container">
      <div className="form__input--wrapper">
        <BsFillXCircleFill
          onClick={clickHandler}
          className="icon-wrapper"
        ></BsFillXCircleFill>

        <form action="localhost:3001" method="post">
          <div className="form__input--name">
            <label for="name">ID를 입력하세요. </label>
            <input type="text" name="name" id="name" required />
          </div>

          <div className="form__input--title">
            <label for="name2">제목을 입력하세요. </label>
            <input type="text" name="name" id="name2" required />
          </div>

          <div className="form__textbox">
            <label for="story">질문 내용을 입력하세요. </label>
            <textarea id="story" name="story" required></textarea>
          </div>
          <div className="form__submit">
            <input
              onClick={registerDiscussion}
              className="'submit"
              type="submit"
              value="작성하기"
            />
          </div>
        </form>
      </div>
    </section>
  );
}
