import { BsFillXCircleFill } from "react-icons/bs";

export default function FormContainer({ setViisiable }) {
  const clickHandler = (e) => {
    if ((e.className = "icon-wrapper")) {
      setViisiable(false);
    }
  };

  return (
    <section className="form__container">
      <div className="form__input--wrapper">
        <BsFillXCircleFill
          onClick={clickHandler}
          className="icon-wrapper"
        ></BsFillXCircleFill>

        <form action="localhost:3001" method="post">
          <div className="form__input--name">
            <label for="name">ID를 입력하세요. </label>
            <input type="text" name="name" id="name" required />
          </div>

          <div className="form__input--title">
            <label for="name2">제목을 입력하세요. </label>
            <input type="text" name="name" id="name2" required />
          </div>

          <div className="form__textbox">
            <label for="story">질문 내용을 입력하세요. </label>
            <textarea id="story" name="story" required></textarea>
          </div>
          <div className="form__submit">
            <input className="'submit" type="submit" value="작성하기" />
          </div>
        </form>
      </div>
    </section>
  );
}
