import { useEffect } from "react";
import { BsFillXCircleFill } from "react-icons/bs";

export default function FormContainer({ setViisiable, isVisiable }) {
  const name = document.querySelector("#name");
  const title = document.querySelector("#name2");
  const content = document.querySelector("#stroy");

  const clickHandler = (e) => {
    if ((e.className = "icon-wrapper")) {
      setViisiable(!isVisiable);
      name.value = "";
      title.value = "";
      content.value = "";
    }
  };

  useEffect(() => {
    function registerDiscussion(event) {
      event.preventDefault();

      const date = new Date();
      const url = "http://localhost:4000/";

      fetch(url, {
        method: "post",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          createdAt: date,
          updatedAt: "2022-04-25T07:45:22Z",
          title: "typeof 강의중 ()가 빠져있는 것일까요?",
          url: "https://github.com/codestates-seb/agora-states-fe/discussions/7",
          author: "namwonjae",
          avatarUrl:
            "https://avatars.githubusercontent.com/u/96907839?s=64&v=4",
        }),
      });
    }
  }, []);

  // document.querySelector('form').addEventListener('submit', registerFood);

  return (
    <section className="form__container">
      <div className="form__input--wrapper">
        <BsFillXCircleFill
          onClick={clickHandler}
          className="icon-wrapper"
        ></BsFillXCircleFill>

        <form method="post">
          <div className="form__input--name">
            <label for="name">ID를 입력하세요. </label>
            <input type="text" name="name" id="name" required />
          </div>

          <div className="form__input--title">
            <label for="name2">제목을 입력하세요. </label>
            <input type="text" name="name" id="name2" required />
          </div>

          <div className="form__textbox">
            <label for="story">질문 내용을 입력하세요. </label>
            <textarea id="story" name="story" required></textarea>
          </div>
          <div className="form__submit">
            <input
              onClick={registerDiscussion}
              className="'submit"
              type="submit"
              value="작성하기"
            />
          </div>
        </form>
      </div>
    </section>
  );
}
